SELECT SUM(V_F_CLAIM_TRANSACTION.CLT_DUE_AMT - (V_F_CLAIM_TRANSACTION.CLT_PATIENT_PAY_AMT - V_F_CLAIM_TRANSACTION.APP_PATIENT_PAY_AMT)) AS LICS__Low_Income_Subsidy__Amount,SUM(V_F_CLAIM_TRANSACTION.CLT_DUE_AMT) AS Amount_Paid
  FROM ORX_IDW_DM_PRD.ADMIN.F_CLAIM_TRANSACTION V_F_CLAIM_TRANSACTION WHERE V_F_CLAIM_TRANSACTION.SBM_DT_SK BETWEEN 20231001 AND 20231031 AND V_F_CLAIM_TRANSACTION.CLAIM_STAT_ID IN ( 'X', 'P' ) and ORIG_CARRIER_ID IN ('EXCGUHCCO') and ORIG_ACCOUNT_ID IN ('E')  and ORIG_EMPLOYER_GROUP_ID IN ('S0001138') and FILLED_DT_SK BETWEEN 20230901 AND 20231031
